mapper.select:
select
  id, ani, dnis, callDirect, fileId, fileName, agentFileName, customFileName, fileInfo, deviceId, agentId, startDateTime, endDateTime, callid, contactId, callLenth, RecordType, serverName, fileEncodingFmt, separableFileEncodingFmt
from filedetail


        and id = #{id}


        and ani = #{ani}


        and dnis = #{dnis}


        and callDirect = #{callDirect}


        and fileId = #{fileId}


        and fileName = #{fileName}


        and agentFileName = #{agentFileName}


        and customFileName = #{customFileName}


        and fileInfo = #{fileInfo}


        and deviceId = #{deviceId}


        and agentId = #{agentId}


        and startDateTime = #{startDateTime}


        and endDateTime = #{endDateTime}


        and callid = #{callid}


        and contactId = #{contactId}


        and callLenth = #{callLenth}


        and RecordType = #{RecordType}


        and serverName = #{serverName}


        and fileEncodingFmt = #{fileEncodingFmt}


        and separableFileEncodingFmt = #{separableFileEncodingFmt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from filedetail


        and id = #{id}


        and ani = #{ani}


        and dnis = #{dnis}


        and callDirect = #{callDirect}


        and fileId = #{fileId}


        and fileName = #{fileName}


        and agentFileName = #{agentFileName}


        and customFileName = #{customFileName}


        and fileInfo = #{fileInfo}


        and deviceId = #{deviceId}


        and agentId = #{agentId}


        and startDateTime = #{startDateTime}


        and endDateTime = #{endDateTime}


        and callid = #{callid}


        and contactId = #{contactId}


        and callLenth = #{callLenth}


        and RecordType = #{RecordType}


        and serverName = #{serverName}


        and fileEncodingFmt = #{fileEncodingFmt}


        and separableFileEncodingFmt = #{separableFileEncodingFmt}
