mapper.select:
select
  id, agentId, agentName, stationId, skillId, skillName, beginTime, endTime, callDuration, callId, relatedCallId, callScenario, callType, callMode, callResult, localMediaIp, domain, originator, originatee, caller, callee, alertTime, answerTime, alertDuration, answerDuration, consultInTimes, consultInSuccTimes, consultOutTimes, consultOutSuccTimes, holdDuration, holdTimes, acwDuration, transFrom, transTo, transOutFlag, satisfyCode, satisfyDuration, gateway, insertTime, updateTime, remoteAgent
from t_agent_detail


        and id = #{id}


        and agentId = #{agentid}


        and agentName = #{agentname}


        and stationId = #{stationid}


        and skillId = #{skillid}


        and skillName = #{skillname}


        and beginTime = #{begintime}


        and endTime = #{endtime}


        and callDuration = #{callduration}


        and callId = #{callid}


        and relatedCallId = #{relatedcallid}


        and callScenario = #{callscenario}


        and callType = #{calltype}


        and callMode = #{callmode}


        and callResult = #{callresult}


        and localMediaIp = #{localmediaip}


        and domain = #{domain}


        and originator = #{originator}


        and originatee = #{originatee}


        and caller = #{caller}


        and callee = #{callee}


        and alertTime = #{alerttime}


        and answerTime = #{answertime}


        and alertDuration = #{alertduration}


        and answerDuration = #{answerduration}


        and consultInTimes = #{consultintimes}


        and consultInSuccTimes = #{consultinsucctimes}


        and consultOutTimes = #{consultouttimes}


        and consultOutSuccTimes = #{consultoutsucctimes}


        and holdDuration = #{holdduration}


        and holdTimes = #{holdtimes}


        and acwDuration = #{acwduration}


        and transFrom = #{transfrom}


        and transTo = #{transto}


        and transOutFlag = #{transoutflag}


        and satisfyCode = #{satisfycode}


        and satisfyDuration = #{satisfyduration}


        and gateway = #{gateway}


        and insertTime = #{inserttime}


        and updateTime = #{updatetime}


        and remoteAgent = #{remoteagent}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_agent_detail


        and id = #{id}


