mapper.select:
select
  id, start_time, end_time, ani, dnis, media_type, call_type, connid, guid, interaction_id, agent_id, agent_name, station_id, queue, dial_count, dial_duration, ring_count, ring_duration, talk_count, talk_duration, hold_count, hold_duration, acw_duration, call_segment_result, create_time, update_time
from t_agent_detail


        and id = #{id}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and ani = #{ani}


        and dnis = #{dnis}


        and media_type = #{mediaType}


        and call_type = #{callType}


        and connid = #{connid}


        and guid = #{guid}


        and interaction_id = #{interactionId}


        and agent_id = #{agentId}


        and agent_name = #{agentName}


        and station_id = #{stationId}


        and queue = #{queue}


        and dial_count = #{dialCount}


        and dial_duration = #{dialDuration}


        and ring_count = #{ringCount}


        and ring_duration = #{ringDuration}


        and talk_count = #{talkCount}


        and talk_duration = #{talkDuration}


        and hold_count = #{holdCount}


        and hold_duration = #{holdDuration}


        and acw_duration = #{acwDuration}


        and call_segment_result = #{callSegmentResult}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_agent_detail


        and id = #{id}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and ani = #{ani}


        and dnis = #{dnis}


        and media_type = #{mediaType}


        and call_type = #{callType}


        and connid = #{connid}


        and guid = #{guid}


        and interaction_id = #{interactionId}


        and agent_id = #{agentId}


        and agent_name = #{agentName}


        and station_id = #{stationId}


        and queue = #{queue}


        and dial_count = #{dialCount}


        and dial_duration = #{dialDuration}


        and ring_count = #{ringCount}


        and ring_duration = #{ringDuration}


        and talk_count = #{talkCount}


        and talk_duration = #{talkDuration}


        and hold_count = #{holdCount}


        and hold_duration = #{holdDuration}


        and acw_duration = #{acwDuration}


        and call_segment_result = #{callSegmentResult}


        and create_time = #{createTime}


        and update_time = #{updateTime}
