        and consultInTotalTimes = #{consultintotaltimes}


        and consultInSuccTotalTimes = #{consultinsucctotaltimes}


        and consultOutTotalTimes = #{consultouttotaltimes}


        and consultOutSuccTotalTimes = #{consultoutsucctotaltimes}


        and holdTotalDuration = #{holdtotalduration}


        and holdTotalTimes = #{holdtotaltimes}


        and conferenceTimes = #{conferencetimes}


        and abandonPoint = #{abandonpoint}


        and abandonType = #{abandontype}


        and hangupSide = #{hangupside}


        and gateway = #{gateway}


        and recordServer = #{recordserver}


        and recordPath = #{recordpath}


        and satisfyCode = #{satisfycode}


        and satisfyDuration = #{satisfyduration}


        and insertTime = #{inserttime}


        and updateTime = #{updatetime}


        and firstSkillTime = #{firstskilltime}


        and firstAgentAlterTime = #{firstagentaltertime}


        and recordFileSuffix = #{recordfilesuffix}


        and remoteAgent = #{remoteagent}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_call_detail


        and id = #{id}


