mapper.select:
select
  id, start_time, end_time, ani, dnis, media_type, call_type, connid, guid, interaction_id, queue_id, queue_duration, queue_result, queue_scene, create_time, update_time
from t_skill_detail


        and id = #{id}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and ani = #{ani}


        and dnis = #{dnis}


        and media_type = #{mediaType}


        and call_type = #{callType}


        and connid = #{connid}


        and guid = #{guid}


        and interaction_id = #{interactionId}


        and queue_id = #{queueId}


        and queue_duration = #{queueDuration}


        and queue_result = #{queueResult}


        and queue_scene = #{queueScene}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_skill_detail


        and id = #{id}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and ani = #{ani}


        and dnis = #{dnis}


        and media_type = #{mediaType}


        and call_type = #{callType}


        and connid = #{connid}


        and guid = #{guid}


        and interaction_id = #{interactionId}


        and queue_id = #{queueId}


        and queue_duration = #{queueDuration}


        and queue_result = #{queueResult}


        and queue_scene = #{queueScene}


        and create_time = #{createTime}


        and update_time = #{updateTime}
